-- UserPhotos
DECLARE @USERPHOTOS_UserIDs TABLE (UserID INT);
DECLARE @USERPHOTOS_TotalUsers INT;
DECLARE @USERPHOTOS_CurrentUserID INT;
DECLARE @USERPHOTOS_RandomPhotoCount INT;
DECLARE @USERPHOTOS_IsSelected BIT;
DECLARE @USERPHOTOS_RandomDays INT;
DECLARE @USERPHOTOS_UploadDate DATETIME;
DECLARE @USERPHOTOS_RandomPhotoNum INT;
DECLARE @USERPHOTOS_Counter INT;

-- Get all user IDs
INSERT INTO @USERPHOTOS_UserIDs (UserID)
SELECT ID FROM Users WHERE IsDeleted = 0;

SELECT @USERPHOTOS_TotalUsers = COUNT(*) FROM @USERPHOTOS_UserIDs;

PRINT 'Processing ' + CAST(@USERPHOTOS_TotalUsers AS VARCHAR(10)) + ' users for both profile and cover photos...';

-- Cursor to process each user
DECLARE USERPHOTOS_user_cursor CURSOR FOR 
SELECT UserID FROM @USERPHOTOS_UserIDs;

OPEN USERPHOTOS_user_cursor;
FETCH NEXT FROM USERPHOTOS_user_cursor INTO @USERPHOTOS_CurrentUserID;

WHILE @@FETCH_STATUS = 0
BEGIN
    -- ===== PROFILE PHOTOS (PhotoTypeID = 1) =====
    
    -- Determine if this user should have a selected profile photo (95% chance)
    SET @USERPHOTOS_IsSelected = CASE WHEN ABS(CHECKSUM(NEWID())) % 100 < 95 THEN 1 ELSE 0 END;
    
    -- Generate random upload date (between 30-365 days ago)
    SET @USERPHOTOS_RandomDays = ABS(CHECKSUM(NEWID())) % 335 + 30;
    SET @USERPHOTOS_UploadDate = DATEADD(DAY, -@USERPHOTOS_RandomDays, GETDATE());
    
    -- Insert primary profile photo (<userid>.jpg)
    INSERT INTO UserPhotos (UserID, PhotoTypeID, PhotoURL, UploadedAt, IsSelected)
    VALUES (@USERPHOTOS_CurrentUserID, 1, CAST(@USERPHOTOS_CurrentUserID AS VARCHAR(10)) + '.jpg', @USERPHOTOS_UploadDate, @USERPHOTOS_IsSelected);
    
    -- Generate 1-4 additional random profile photos
    SET @USERPHOTOS_RandomPhotoCount = ABS(CHECKSUM(NEWID())) % 4 + 1;
    SET @USERPHOTOS_Counter = 0;
    
    WHILE @USERPHOTOS_Counter < @USERPHOTOS_RandomPhotoCount
    BEGIN
        -- Generate random photo number (1 to total users)
        SET @USERPHOTOS_RandomPhotoNum = ABS(CHECKSUM(NEWID())) % @USERPHOTOS_TotalUsers + 1;
        
        -- Generate different upload date for each additional photo
        SET @USERPHOTOS_RandomDays = ABS(CHECKSUM(NEWID())) % 335 + 30;
        SET @USERPHOTOS_UploadDate = DATEADD(DAY, -@USERPHOTOS_RandomDays, GETDATE());
        
        -- Insert additional photo (always IsSelected = 0 for additional photos)
        INSERT INTO UserPhotos (UserID, PhotoTypeID, PhotoURL, UploadedAt, IsSelected)
        VALUES (@USERPHOTOS_CurrentUserID, 1, CAST(@USERPHOTOS_RandomPhotoNum AS VARCHAR(10)) + '.jpg', @USERPHOTOS_UploadDate, 0);
        
        SET @USERPHOTOS_Counter = @USERPHOTOS_Counter + 1;
    END;
    
    -- ===== COVER PHOTOS (PhotoTypeID = 2) =====
    
    -- Determine if this user should have a selected cover photo (95% chance)
    SET @USERPHOTOS_IsSelected = CASE WHEN ABS(CHECKSUM(NEWID())) % 100 < 95 THEN 1 ELSE 0 END;
    
    -- Generate random upload date (between 30-365 days ago)
    SET @USERPHOTOS_RandomDays = ABS(CHECKSUM(NEWID())) % 335 + 30;
    SET @USERPHOTOS_UploadDate = DATEADD(DAY, -@USERPHOTOS_RandomDays, GETDATE());
    
    -- Insert primary cover photo (<userid>.jpg)
    INSERT INTO UserPhotos (UserID, PhotoTypeID, PhotoURL, UploadedAt, IsSelected)
    VALUES (@USERPHOTOS_CurrentUserID, 2, CAST(@USERPHOTOS_CurrentUserID AS VARCHAR(10)) + '.jpg', @USERPHOTOS_UploadDate, @USERPHOTOS_IsSelected);
    
    -- Generate 1-3 additional random cover photos
    SET @USERPHOTOS_RandomPhotoCount = ABS(CHECKSUM(NEWID())) % 3 + 1;
    SET @USERPHOTOS_Counter = 0;
    
    WHILE @USERPHOTOS_Counter < @USERPHOTOS_RandomPhotoCount
    BEGIN
        -- Generate random photo number (1 to 1033 for cover photos)
        SET @USERPHOTOS_RandomPhotoNum = ABS(CHECKSUM(NEWID())) % 1033 + 1;
        
        -- Generate different upload date for each additional photo
        SET @USERPHOTOS_RandomDays = ABS(CHECKSUM(NEWID())) % 335 + 30;
        SET @USERPHOTOS_UploadDate = DATEADD(DAY, -@USERPHOTOS_RandomDays, GETDATE());
        
        -- Insert additional photo (always IsSelected = 0 for additional photos)
        INSERT INTO UserPhotos (UserID, PhotoTypeID, PhotoURL, UploadedAt, IsSelected)
        VALUES (@USERPHOTOS_CurrentUserID, 2, CAST(@USERPHOTOS_RandomPhotoNum AS VARCHAR(10)) + '.jpg', @USERPHOTOS_UploadDate, 0);
        
        SET @USERPHOTOS_Counter = @USERPHOTOS_Counter + 1;
    END;
    
    FETCH NEXT FROM USERPHOTOS_user_cursor INTO @USERPHOTOS_CurrentUserID;
END;

CLOSE USERPHOTOS_user_cursor;
DEALLOCATE USERPHOTOS_user_cursor;

PRINT 'Both profile and cover photos insertion completed!';

-- Verify results for Profile Photos
SELECT 
    'Profile Photos Summary' AS Summary,
    COUNT(*) AS TotalEntries,
    SUM(CASE WHEN IsSelected = 1 THEN 1 ELSE 0 END) AS SelectedPhotos,
    COUNT(DISTINCT UserID) AS UsersWithPhotos
FROM UserPhotos 
WHERE PhotoTypeID = 1;

-- Verify results for Cover Photos
SELECT 
    'Cover Photos Summary' AS Summary,
    COUNT(*) AS TotalEntries,
    SUM(CASE WHEN IsSelected = 1 THEN 1 ELSE 0 END) AS SelectedPhotos,
    COUNT(DISTINCT UserID) AS UsersWithPhotos
FROM UserPhotos 
WHERE PhotoTypeID = 2;

-- Overall summary
SELECT 
    PhotoTypeID,
    CASE 
        WHEN PhotoTypeID = 1 THEN 'Profile Photos'
        WHEN PhotoTypeID = 2 THEN 'Cover Photos'
        ELSE 'Other'
    END AS PhotoType,
    COUNT(*) AS TotalPhotos,
    SUM(CASE WHEN IsSelected = 1 THEN 1 ELSE 0 END) AS SelectedPhotos,
    COUNT(DISTINCT UserID) AS UsersWithPhotos
FROM UserPhotos 
WHERE PhotoTypeID IN (1, 2)
GROUP BY PhotoTypeID
ORDER BY PhotoTypeID;

GO



-- Friendships
DECLARE @FRIENDSHIP_UserIDs TABLE (UserID INT);
DECLARE @FRIENDSHIP_TotalUsers INT;
DECLARE @FRIENDSHIP_CurrentUserID INT;
DECLARE @FRIENDSHIP_TargetUserID INT;
DECLARE @FRIENDSHIP_FriendshipCount INT;
DECLARE @FRIENDSHIP_StatusID INT;
DECLARE @FRIENDSHIP_RandomDays INT;
DECLARE @FRIENDSHIP_RequestedDate DATETIME;
DECLARE @FRIENDSHIP_AcceptedDate DATETIME;
DECLARE @FRIENDSHIP_Counter INT;
DECLARE @FRIENDSHIP_AcceptedFriends INT;
DECLARE @FRIENDSHIP_MaxAttempts INT;
DECLARE @FRIENDSHIP_Attempts INT;

-- Get all user IDs
INSERT INTO @FRIENDSHIP_UserIDs (UserID)
SELECT ID FROM Users WHERE IsDeleted = 0;

SELECT @FRIENDSHIP_TotalUsers = COUNT(*) FROM @FRIENDSHIP_UserIDs;

PRINT 'Processing ' + CAST(@FRIENDSHIP_TotalUsers AS VARCHAR(10)) + ' users for friendship connections...';

-- Cursor to process each user
DECLARE friendship_cursor CURSOR FOR 
SELECT UserID FROM @FRIENDSHIP_UserIDs;

OPEN friendship_cursor;
FETCH NEXT FROM friendship_cursor INTO @FRIENDSHIP_CurrentUserID;

WHILE @@FETCH_STATUS = 0
BEGIN
    -- Generate 7-15 accepted friends for this user
    SET @FRIENDSHIP_AcceptedFriends = ABS(CHECKSUM(NEWID())) % 9 + 7; -- 7 to 15
    SET @FRIENDSHIP_Counter = 0;
    SET @FRIENDSHIP_MaxAttempts = @FRIENDSHIP_AcceptedFriends * 3; -- Prevent infinite loops
    SET @FRIENDSHIP_Attempts = 0;
    
    -- Create accepted friendships
    WHILE @FRIENDSHIP_Counter < @FRIENDSHIP_AcceptedFriends AND @FRIENDSHIP_Attempts < @FRIENDSHIP_MaxAttempts
    BEGIN
        SET @FRIENDSHIP_Attempts = @FRIENDSHIP_Attempts + 1;
        
        -- Select random target user (not self)
        SET @FRIENDSHIP_TargetUserID = (
            SELECT TOP 1 UserID 
            FROM @FRIENDSHIP_UserIDs 
            WHERE UserID != @FRIENDSHIP_CurrentUserID 
            ORDER BY NEWID()
        );
        
        -- Check if friendship already exists (either direction)
        IF NOT EXISTS (
            SELECT 1 FROM Friendships 
            WHERE (SenderID = @FRIENDSHIP_CurrentUserID AND ReceiverID = @FRIENDSHIP_TargetUserID)
               OR (SenderID = @FRIENDSHIP_TargetUserID AND ReceiverID = @FRIENDSHIP_CurrentUserID)
        )
        BEGIN
            -- Generate random request date (June 2023 to June 2025)
            SET @FRIENDSHIP_RandomDays = ABS(CHECKSUM(NEWID())) % 731; -- ~2 years in days
            SET @FRIENDSHIP_RequestedDate = DATEADD(DAY, @FRIENDSHIP_RandomDays, '2023-06-01');
            
            -- Generate accepted date (1-30 days after request)
            SET @FRIENDSHIP_RandomDays = ABS(CHECKSUM(NEWID())) % 30 + 1;
            SET @FRIENDSHIP_AcceptedDate = DATEADD(DAY, @FRIENDSHIP_RandomDays, @FRIENDSHIP_RequestedDate);
            
            -- Insert accepted friendship
            INSERT INTO Friendships (SenderID, ReceiverID, FriendshipStatusID, RequestedAt, AcceptedAt)
            VALUES (@FRIENDSHIP_CurrentUserID, @FRIENDSHIP_TargetUserID, 2, @FRIENDSHIP_RequestedDate, @FRIENDSHIP_AcceptedDate);
            
            SET @FRIENDSHIP_Counter = @FRIENDSHIP_Counter + 1;
        END;
    END;
    
    -- Generate additional random friendship requests (pending/declined)
    SET @FRIENDSHIP_FriendshipCount = ABS(CHECKSUM(NEWID())) % 5 + 2; -- 2-6 additional requests
    SET @FRIENDSHIP_Counter = 0;
    SET @FRIENDSHIP_MaxAttempts = @FRIENDSHIP_FriendshipCount * 3;
    SET @FRIENDSHIP_Attempts = 0;
    
    WHILE @FRIENDSHIP_Counter < @FRIENDSHIP_FriendshipCount AND @FRIENDSHIP_Attempts < @FRIENDSHIP_MaxAttempts
    BEGIN
        SET @FRIENDSHIP_Attempts = @FRIENDSHIP_Attempts + 1;
        
        -- Select random target user (not self)
        SET @FRIENDSHIP_TargetUserID = (
            SELECT TOP 1 UserID 
            FROM @FRIENDSHIP_UserIDs 
            WHERE UserID != @FRIENDSHIP_CurrentUserID 
            ORDER BY NEWID()
        );
        
        -- Check if friendship already exists (either direction)
        IF NOT EXISTS (
            SELECT 1 FROM Friendships 
            WHERE (SenderID = @FRIENDSHIP_CurrentUserID AND ReceiverID = @FRIENDSHIP_TargetUserID)
               OR (SenderID = @FRIENDSHIP_TargetUserID AND ReceiverID = @FRIENDSHIP_CurrentUserID)
        )
        BEGIN
            -- Generate random status (1=Pending, 3=Declined)
            SET @FRIENDSHIP_StatusID = CASE WHEN ABS(CHECKSUM(NEWID())) % 2 = 0 THEN 1 ELSE 3 END;
            
            -- Generate random request date (June 2023 to June 2025)
            SET @FRIENDSHIP_RandomDays = ABS(CHECKSUM(NEWID())) % 731; -- ~2 years in days
            SET @FRIENDSHIP_RequestedDate = DATEADD(DAY, @FRIENDSHIP_RandomDays, '2023-06-01');
            
            -- Set AcceptedAt to NULL for non-accepted statuses
            SET @FRIENDSHIP_AcceptedDate = NULL;
            
            -- Insert friendship request
            INSERT INTO Friendships (SenderID, ReceiverID, FriendshipStatusID, RequestedAt, AcceptedAt)
            VALUES (@FRIENDSHIP_CurrentUserID, @FRIENDSHIP_TargetUserID, @FRIENDSHIP_StatusID, @FRIENDSHIP_RequestedDate, @FRIENDSHIP_AcceptedDate);
            
            SET @FRIENDSHIP_Counter = @FRIENDSHIP_Counter + 1;
        END;
    END;
    
    FETCH NEXT FROM friendship_cursor INTO @FRIENDSHIP_CurrentUserID;
END;

CLOSE friendship_cursor;
DEALLOCATE friendship_cursor;

PRINT 'Friendship generation completed!';

-- Overall summary
SELECT 
    FriendshipStatusID,
    CASE 
        WHEN FriendshipStatusID = 1 THEN 'Pending'
        WHEN FriendshipStatusID = 2 THEN 'Accepted'
        WHEN FriendshipStatusID = 3 THEN 'Declined'
        ELSE 'Other'
    END AS FriendshipStatus,
    COUNT(*) AS TotalFriendships,
    COUNT(DISTINCT SenderID) AS UniqueSenders,
    COUNT(DISTINCT ReceiverID) AS UniqueReceivers,
    CASE 
        WHEN COUNT(DISTINCT SenderID) > 0 
        THEN CAST(COUNT(*) AS FLOAT) / COUNT(DISTINCT SenderID)
        ELSE 0 
    END AS AvgFriendshipsPerSender
FROM Friendships 
WHERE FriendshipStatusID IN (1, 2, 3)
GROUP BY FriendshipStatusID
ORDER BY FriendshipStatusID;